mapper.select:
SELECT u.id, u.username, u.`status`, u.phone, u.login_count AS loginCount, u.last_login_time AS lastLoginTime, u.create_time AS createTime, u.head_img_url AS headImgUrl, GROUP_CONCAT(r.id ORDER BY u.id) roleIds, GROUP_CONCAT(r.role_name ORDER BY u.id) roleNames FROM sys_user u LEFT JOIN sys_user_role_rel ur ON ur.user_id = u.id LEFT JOIN sys_role r ON r.id = ur.role_id
AND u.status = #{params.status}
AND ur.role_id = #{params.roleId}
AND (u.username LIKE CONCAT(CONCAT('%',#{params.fuzzySearch},'%')) OR u.phone LIKE CONCAT(CONCAT('%',#{params.fuzzySearch},'%')))
GROUP BY u.id ORDER BY u.id DESC
